#include "minishell.h"

int ms_is_valid_key(const char *s)
{
	size_t i;

	if (!s || !(ft_isalpha((unsigned char)s[0]) || s[0] == '_'))
	    return (0);
	i = 1;
	while (s[i])
	{
	    if (!(ft_isalnum((unsigned char)s[i]) || s[i] == '_'))
	        return (0);
	    i++;
	}
	return (1);
}
